graphql.query:
query MysqlTitles {
    mysqlTitles(skip: 0, take: 20) {
        totalCount
        items {
            titleId
            titleType
            primaryTitle
            originalTitle
            isAdult
            startYear
            endYear
            runtimeMinutes
        }
    }
}

mutation CreateMysqlTitle {
    createMysqlTitle(
        input: {
            title: {
                titleType: "movie"
                primaryTitle: "The Movie"
                originalTitle: "The Movie"
                isAdult: true
                startYear: 2025
                endYear: null
                runtimeMinutes: 50
            }
        }
    ) {
        errors {
            ... on ArgumentError {
                message
                paramName
            }
            ... on InvalidOperationError {
                message
            }
        }
        titles {
            titleId
            titleType
            primaryTitle
            originalTitle
            isAdult
            startYear
            endYear
            runtimeMinutes
        }
    }
}

query MysqlTitle {
    mysqlTitles( where: { titleId: {eq: "{{titleId}}"}}) {
        items {
            titleId
            titleType
            primaryTitle
            originalTitle
            isAdult
            startYear
            endYear
            runtimeMinutes
        }
    }
}

mutation DeleteMysqlTitle {
    deleteMysqlTitle(input: { id: "{{titleId}}" }) {
        titles {
            titleId
            titleType
            primaryTitle
            originalTitle
            isAdult
            startYear
            endYear
            runtimeMinutes
        }
    }
}

